mapper.select:
SELECT m.tree_ename as "parent", m.node_ename as "label", m.node_cname as "text", m.node_image_path as "imagePath", m.node_type as "leaf", m.node_url as "url", p.id as "id" FROM fui_menu m left join fui_permissions p on CONCAT( CONCAT( m.tree_ename, '.' ), m.node_ename ) = p.code WHERE 1=1
and m.tree_ename = #{node}
order by m.tree_ename asc, m.node_sort_id asc, m.node_ename asc

SELECT rec_creator as "recCreator", rec_create_time as "recCreateTime", rec_revisor as "recRevisor", rec_revise_time as "recReviseTime", archive_flag as "archiveFlag", tree_ename as "pid", node_ename as "id", node_cname as "text", node_type as "type", node_url as "url", node_sort_id as "sortId", node_param as "param", node_image_path as "image" FROM fui_menu WHERE 1=1
and tree_ename = #{id}
and node_ename = #{node}
and node_url = #{nodeUrl}
order by tree_ename asc, node_sort_id asc, node_ename asc

mapper.update:
 delete from
 	fui_menu
	where tree_ename=#{pid} and node_ename=#{id}
