mapper.select:
SELECT REC_CREATOR as "recCreator", REC_CREATE_TIME as "recCreateTime", REC_REVISOR as "recRevisor", REC_REVISE_TIME as "recReviseTime", ARCHIVE_FLAG as "archiveFlag", FORM_ENAME as "formEname", FORM_CNAME as "formCname", FORM_LOAD_PATH as "formLoadPath", FORM_TYPE as "formType", MODULE_ENAME_1 as "moduleEname1", MODULE_ENAME_2 as "moduleEname2", IS_AUTH as "isAuth", FORM_PARAM as "formParam" FROM ${platSchema}.fui_page_info WHERE 1=1
AND FORM_ENAME = #{formEname}
ORDER BY #{orderBy}
ORDER BY FORM_ENAME asc
